input.value:
timeseries cpu_usages = avg(dt.kubernetes.container.cpu_usage), by:{ dt.entity.cloud_application_instance}
| lookup [
fetch dt.entity.cloud_application_instance
], sourceField:dt.entity.cloud_application_instance, lookupField:id , fields:{ entity.name}
| fieldsAdd cpu=arrayAvg(cpu_usages)
| filter startsWith(entity.name,"kubearmor-")
| fieldsRemove cpu_usages, dt.entity.cloud_application_instance

